INSERT INTO dbo.[RefIPEDSFASBRevenueRestriction]([Code], [Description], [Definition], [SortOrder])
VALUES ('TemporarilyRestricted', 'Temporarily Restricted', 'Used for reporting revenues that are subject to limitation by donor specification as to use or the time when use may occur (such as a later period of time or after specified events have occurred).', NULL)
INSERT INTO dbo.[RefIPEDSFASBRevenueRestriction]([Code], [Description], [Definition], [SortOrder])
