PRINT N'Populate RefIPEDSFASBScholarshipsandFellowshipsRevenue table';
IF EXISTS(SELECT * FROM dbo.sysobjects WHERE id = object_id(N'[dbo].[RefIPEDSFASBScholarshipsandFellowshipsRevenue]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
BEGIN
INSERT INTO dbo.[RefIPEDSFASBScholarshipsandFellowshipsRevenue]([Code], [Description], [Definition], [SortOrder])
VALUES ('Discountsallowancesappliedtoauxiliary', 'Discounts and allowances applied to auxiliary enterprise revenues', 'The amount of allowances (scholarships) applied to auxiliary enterprise revenues (e.g., dormitory charges).', NULL)
INSERT INTO dbo.[RefIPEDSFASBScholarshipsandFellowshipsRevenue]([Code], [Description], [Definition], [SortOrder])
VALUES ('Discountsallowancesappliedtotuitionfees', 'Discounts and allowances applied to tuition and fees', 'The amount of allowances (scholarships) applied to tuition and fees.', NULL)
INSERT INTO dbo.[RefIPEDSFASBScholarshipsandFellowshipsRevenue]([Code], [Description], [Definition], [SortOrder])
VALUES ('Grantsbylocalgovernment', 'Grants by local government', 'Local government grants include scholarships or gift-aid awarded directly to the student.', NULL)
INSERT INTO dbo.[RefIPEDSFASBScholarshipsandFellowshipsRevenue]([Code], [Description], [Definition], [SortOrder])
VALUES ('Grantsbystategovernment', 'Grants by state government', 'Grant monies provided by the state such as Leveraging Educational Assistance Partnerships (LEAP) (formerly SSIG''s); merit scholarships provided by the state; and tuition and fee waivers for which the institution was reimbursed by a state agency.', NULL)
INSERT INTO dbo.[RefIPEDSFASBScholarshipsandFellowshipsRevenue]([Code], [Description], [Definition], [SortOrder])
VALUES ('Institutionalgrantsrestricted', 'Institutional grants (restricted)', 'Scholarships and fellowships awarded to students from institutional resources that are restricted to student aid. Private institutions generally report these grants as allowances. If control over these resources passes to the student, the amount is reported as an expense.', NULL)
INSERT INTO dbo.[RefIPEDSFASBScholarshipsandFellowshipsRevenue]([Code], [Description], [Definition], [SortOrder])
VALUES ('Institutionalgrantsunrestricted', 'Institutional grants (unrestricted)', 'Scholarships and fellowships awarded to students from unrestricted institutional resources. Private institutions generally report these grants as allowances. If control over these resources passes to the student, the amount is reported as an expense.', NULL)
INSERT INTO dbo.[RefIPEDSFASBScholarshipsandFellowshipsRevenue]([Code], [Description], [Definition], [SortOrder])
VALUES ('Otherfederalgrants', 'Other federal grants', 'Federal monies awarded to the institution under federal government student aid programs, such as the Federal Supplemental Educational Opportunity Grants (FSEOG), DHHS training grants (aid portion only), the Leveraging Education Assistance Partnership (LEAP) program, and other federal student aid programs. Pell Grants are not included in this classification. Note: if the federal government selects the student recipients and simply transmits the funds to the institution for disbursement to the student, the amounts are not considered as revenues and subsequently there are no discounts and allowances or scholarships and fellowships expenses. If the funds are made available to the institution for selection of student recipients, then the amounts received are considered as nonoperating revenues and subsequently as discounts and allowances or scholarships and fellowships expenses.', NULL)
INSERT INTO dbo.[RefIPEDSFASBScholarshipsandFellowshipsRevenue]([Code], [Description], [Definition], [SortOrder])
VALUES ('Pellgrants', 'Pell grants', 'The gross amount of Pell Grants made available to recipients by your institution. This is the gross Pell Grants received as federal grant revenue for the fiscal year.', NULL)
END;

PRINT N'Populate RefIPEDSGASBFinancialPosition table';
IF EXISTS(SELECT * FROM dbo.sysobjects WHERE id = object_id(N'[dbo].[RefIPEDSGASBFinancialPosition]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
BEGIN
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('CapitalAssetsAccumulateddepreciation', 'Capital Assets: Accumulated depreciation', 'Used for reporting all depreciation amounts, including depreciation on assets that may not be included in other capital assets categories.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('CapitalAssetsBuildings', 'Capital Assets: Buildings', 'Used for reporting structures built for occupancy or use, such as for classrooms, research, administrative offices, storage, etc. Includes built-in fixtures and equipment that are essentially part of the permanent structure.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('CapitalAssetsConstructioninprogress', 'Capital Assets: Construction in progress', 'Used for reporting capital assets under construction and not yet placed into service.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('CapitalAssetsEquipmentincludingartlibrary', 'Capital Assets: Equipment, including art and library collections', 'Used for reporting moveable tangible property such as research equipment, vehicles, office equipment, library collections (capitalized amount of books, films, tapes, and other materials maintained in library collections intended for use by patrons), and capitalized art collections.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('CapitalAssetsInfrastructure', 'Capital Assets: Infrastructure', 'Used for reporting infrastructure assets such as roads, bridges, drainage systems, water and sewer systems, etc.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('CapitalAssetsIntangibleassetsnetof', 'Capital Assets: Intangible assets, net of accumulated amortization', 'Used for reporting all assets consisting of certain nonmaterial rights and benefits of an institution, such as patents, copyrights, trademarks and goodwill. The amount should be reduced by total accumulated amortization.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('CapitalAssetsLandlandimprovements', 'Capital Assets: Land and land improvements', 'Used for reporting land and other land improvements, such as athletic fields, golf courses, lakes, etc.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('CapitalAssetsOthercapitalassets', 'Capital Assets: Other capital assets', NULL, NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('Deferredinflowsofresources', 'Deferred inflows of resources', 'Used for reporting the deferred inflows of resources as recognized in the institution?s GPFS and in accordance with GASB 63.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('Deferredoutflowsofresources', 'Deferred outflows of resources', 'Used for reporting the deferred outflows of resources as recognized in the institution?s GPFS and in accordance with GASB 63.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('Depreciablecapitalassetsnetofdepreciation', 'Depreciable capital assets, net of depreciation', 'Used for reporting all capital assets reduced by the total accumulated depreciation, including improvements to land, easements, buildings, building improvements, vehicles, machinery, equipment, infrastructure, and all other tangible or intangible depreciable assets that are used in operations and that have initial useful lives extending beyond a single reporting period. Includes the net amount of all depreciable capital assets after reducing the gross amount for accumulated depreciation.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('Longtermdebt', 'Long-term debt', 'Used for reporting the amount for long-term debt arising from debt issuance and lease-purchase agreements.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('Longtermdebtcurrentportion', 'Long-term debt, current portion', 'Used for reporting the amount due in the next operating cycle (usually a year) for amounts otherwise reported as long-term or noncurrent debt. Includes only outstanding debt.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('Netassetsinvestedincapitalassetsnetofrelateddebt', 'Net assets invested in capital assets, net of related debt', 'Used for reporting the component of net assets represented by the total of all capital assets, reduced by accumulated depreciation, and reduced by the amount of outstanding bonds, mortgages, notes, or other borrowings that are attributable to the acquisition, construction, or improvement of those assets (see indebtedness on capital assets). Some outstanding debt may be reported in both current and noncurrent liabilities.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('Othercurrentliabilities', 'Other current liabilities', NULL, NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('Othernoncurrentassets', 'Other noncurrent assets', NULL, NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('Othernoncurrentliabilities', 'Other non-current liabilities', NULL, NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('Restrictedexpendablenetassets', 'Restricted expendable net assets', 'Used for reporting restricted net assets that are expendable. Net assets should be reported as restricted when constraints placed on use are either (a) externally imposed by creditors, grantors, contributors, or laws and regulations of other governments or (b) imposed by law through constitutional provisions or enabling legislation. Expendable net assets are all those not required to be retained in perpetuity.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('Restrictednonexpendablenetassets', 'Restricted non-expendable net assets', 'Used for reporting net assets that are restricted and nonexpendable. Net assets should be reported as restricted when constraints placed on use are either (a) externally imposed by creditors, grantors, contributors, or laws and regulations of other governments or (b) imposed by law through constitutional provisions or enabling legislation. Nonexpendable net assets are those that are required to be retained in perpetuity.', NULL)
INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
