INSERT INTO dbo.[RefIPEDSGASBFinancialPosition]([Code], [Description], [Definition], [SortOrder])
VALUES ('Unrestrictednetassets', 'Unrestricted net assets', 'This should be the amount of net assets that do not meet the definition of ?restricted? or ?invested in capital assets, net of related debt.?', NULL)
