INSERT INTO dbo.[Ref_Financial_Expenditure_Level_Of_Instruction_Code]([Code], [Description], [Definition], [Sort_Order])
VALUES ('10', 'Elementary', 'A school organization classified as elementary by state and local practice and composed of any span of grades from Pre_Kindergarten_, kindergarten, or grade 1 through grade 8.', 1.00)
INSERT INTO dbo.[Ref_Financial_Expenditure_Level_Of_Instruction_Code]([Code], [Description], [Definition], [Sort_Order])
VALUES ('11', 'Pre_Kindergarten_', 'A school organization composed entirely of any span of ages below kindergarten. This category is normally used for students identified as needing special services, although regular education Pre_Kindergarten_ would also be appropriate.', 2.00)
INSERT INTO dbo.[Ref_Financial_Expenditure_Level_Of_Instruction_Code]([Code], [Description], [Definition], [Sort_Order])
VALUES ('12', 'Kindergarten', 'A school organization for children in the year immediately preceding first grade.', 3.00)
INSERT INTO dbo.[Ref_Financial_Expenditure_Level_Of_Instruction_Code]([Code], [Description], [Definition], [Sort_Order])
VALUES ('19', 'Other Elementary', 'A school organization composed of any span of grades not above grade 8, except Pre_Kindergarten_ and kindergarten.', 4.00)
INSERT INTO dbo.[Ref_Financial_Expenditure_Level_Of_Instruction_Code]([Code], [Description], [Definition], [Sort_Order])
VALUES ('20', 'Middle', 'A school organization composed of at least three grades, usually beginning with grade 4 through grade 6 or the equivalent and usually ending with grade 8 or grade 9. Most middle school organizations presume that students finishing middle school will go on to secondary school.', 5.00)
INSERT INTO dbo.[Ref_Financial_Expenditure_Level_Of_Instruction_Code]([Code], [Description], [Definition], [Sort_Order])
VALUES ('30', 'Secondary', 'A school organization comprising any span of grades beginning with the next grade following an elementary or middle school, usually beginning with grade 7 through grade 10, and ending with or below grade 12.', 6.00)
INSERT INTO dbo.[Ref_Financial_Expenditure_Level_Of_Instruction_Code]([Code], [Description], [Definition], [Sort_Order])
VALUES ('37', 'Elementary and Secondary Combined', 'A school organization comprising elementary and secondary levels of instruction, which may also include kindergarten and Pre_Kindergarten_ programs.', 7.00)
INSERT INTO dbo.[Ref_Financial_Expenditure_Level_Of_Instruction_Code]([Code], [Description], [Definition], [Sort_Order])
VALUES ('40', 'Postsecondary', 'A school organization providing formal instructional programs with a curriculum designed primarily for students who have completed the requirements for a high school diploma or the equivalent. This includes programs of an academic, vocational, and continuing professional education purpose and excludes avocational and adult education programs.', 8.00)
INSERT INTO dbo.[Ref_Financial_Expenditure_Level_Of_Instruction_Code]([Code], [Description], [Definition], [Sort_Order])
VALUES ('41', 'Programs for Adult/_Continuing', 'A program for adults and out-of-school youth (typically 16 years of age and older who are not regularly enrolled in school) who have completed, interrupted, or not begun their formal education and are pursuing skills or knowledge in other than regularly prescribed courses. These include adult basic education programs and other programs for the pursuit of special interests or enrichment.', 9.00)
INSERT INTO dbo.[Ref_Financial_Expenditure_Level_Of_Instruction_Code]([Code], [Description], [Definition], [Sort_Order])
VALUES ('42', 'Community/_Junior College', 'An institution of higher education that usually offers the first 2 years of college instruction and frequently grants an associate''s degree, but does not grant a bachelor''s degree. It is an independently organized institution (public or nonpublic), an institution that is part of a school district, or an independently organized system of junior colleges. Junior colleges offer college transfer courses and programs; vocational, technical, and semiprofessional occupational programs; or general education programs.', 10.00)
INSERT INTO dbo.[Ref_Financial_Expenditure_Level_Of_Instruction_Code]([Code], [Description], [Definition], [Sort_Order])
VALUES ('50', 'School Wide', 'For coding expenditures that cannot be clearly assigned to a specific instructional level.', 11.00)
END;

