INSERT INTO dbo.[Ref_IPEDSFASB_Revenue_Restriction]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Temporarily_Restricted', 'Temporarily Restricted', 'Used for reporting revenues that are subject to limitation by donor specification as to use or the time when use may occur (such as a later period of time or after specified events have occurred).', NULL)
INSERT INTO dbo.[Ref_IPEDSFASB_Revenue_Restriction]([Code], [Description], [Definition], [Sort_Order])
