PRINT N'Populate Ref_IPEDSFASB_Scholarshipsand_Fellowships_Revenue table';
IF EXISTS(SELECT * FROM dbo.sysobjects WHERE id = object_id(N'[dbo].[Ref_IPEDSFASB_Scholarshipsand_Fellowships_Revenue]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
BEGIN
INSERT INTO dbo.[Ref_IPEDSFASB_Scholarshipsand_Fellowships_Revenue]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Discountsallowancesappliedtoauxiliary', 'Discounts and allowances applied to auxiliary enterprise revenues', 'The amount of allowances (scholarships) applied to auxiliary enterprise revenues (e.g., dormitory charges).', NULL)
INSERT INTO dbo.[Ref_IPEDSFASB_Scholarshipsand_Fellowships_Revenue]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Discountsallowancesappliedtotuitionfees', 'Discounts and allowances applied to tuition and fees', 'The amount of allowances (scholarships) applied to tuition and fees.', NULL)
INSERT INTO dbo.[Ref_IPEDSFASB_Scholarshipsand_Fellowships_Revenue]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Grantsbylocalgovernment', 'Grants by local government', 'Local government grants include scholarships or gift-aid awarded directly to the student.', NULL)
INSERT INTO dbo.[Ref_IPEDSFASB_Scholarshipsand_Fellowships_Revenue]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Grantsbystategovernment', 'Grants by state government', 'Grant monies provided by the state such as Leveraging Educational Assistance Partnerships (LEAP) (formerly SSIG''s); merit scholarships provided by the state; and tuition and fee waivers for which the institution was reimbursed by a state agency.', NULL)
INSERT INTO dbo.[Ref_IPEDSFASB_Scholarshipsand_Fellowships_Revenue]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Institutionalgrantsrestricted', 'Institutional grants (restricted)', 'Scholarships and fellowships awarded to students from institutional resources that are restricted to student aid. Private institutions generally report these grants as allowances. If control over these resources passes to the student, the amount is reported as an expense.', NULL)
INSERT INTO dbo.[Ref_IPEDSFASB_Scholarshipsand_Fellowships_Revenue]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Institutionalgrantsunrestricted', 'Institutional grants (unrestricted)', 'Scholarships and fellowships awarded to students from unrestricted institutional resources. Private institutions generally report these grants as allowances. If control over these resources passes to the student, the amount is reported as an expense.', NULL)
INSERT INTO dbo.[Ref_IPEDSFASB_Scholarshipsand_Fellowships_Revenue]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Otherfederalgrants', 'Other federal grants', 'Federal monies awarded to the institution under federal government student aid programs, such as the Federal Supplemental Educational Opportunity Grants (FSEOG), DHHS training grants (aid portion only), the Leveraging Education Assistance Partnership (LEAP) program, and other federal student aid programs. Pell Grants are not included in this classification. Note: if the federal government selects the student recipients and simply transmits the funds to the institution for disbursement to the student, the amounts are not considered as revenues and subsequently there are no discounts and allowances or scholarships and fellowships expenses. If the funds are made available to the institution for selection of student recipients, then the amounts received are considered as nonoperating revenues and subsequently as discounts and allowances or scholarships and fellowships expenses.', NULL)
INSERT INTO dbo.[Ref_IPEDSFASB_Scholarshipsand_Fellowships_Revenue]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Pellgrants', 'Pell grants', 'The gross amount of Pell Grants made available to recipients by your institution. This is the gross Pell Grants received as federal grant revenue for the fiscal year.', NULL)
END;

PRINT N'Populate Ref_IPEDSGASB_Financial_Position table';
IF EXISTS(SELECT * FROM dbo.sysobjects WHERE id = object_id(N'[dbo].[Ref_IPEDSGASB_Financial_Position]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
BEGIN
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Capital_Assets_Accumulateddepreciation', 'Capital Assets: Accumulated depreciation', 'Used for reporting all depreciation amounts, including depreciation on assets that may not be included in other capital assets categories.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Capital_Assets_Buildings', 'Capital Assets: Buildings', 'Used for reporting structures built for occupancy or use, such as for classrooms, research, administrative offices, storage, etc. Includes built-in fixtures and equipment that are essentially part of the permanent structure.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Capital_Assets_Constructioninprogress', 'Capital Assets: Construction in progress', 'Used for reporting capital assets under construction and not yet placed into service.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Capital_Assets_Equipmentincludingartlibrary', 'Capital Assets: Equipment, including art and library collections', 'Used for reporting moveable tangible property such as research equipment, vehicles, office equipment, library collections (capitalized amount of books, films, tapes, and other materials maintained in library collections intended for use by patrons), and capitalized art collections.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Capital_Assets_Infrastructure', 'Capital Assets: Infrastructure', 'Used for reporting infrastructure assets such as roads, bridges, drainage systems, water and sewer systems, etc.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Capital_Assets_Intangibleassetsnetof', 'Capital Assets: Intangible assets, net of accumulated amortization', 'Used for reporting all assets consisting of certain nonmaterial rights and benefits of an institution, such as patents, copyrights, trademarks and goodwill. The amount should be reduced by total accumulated amortization.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Capital_Assets_Landlandimprovements', 'Capital Assets: Land and land improvements', 'Used for reporting land and other land improvements, such as athletic fields, golf courses, lakes, etc.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Capital_Assets_Othercapitalassets', 'Capital Assets: Other capital assets', NULL, NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Deferredinflowsofresources', 'Deferred inflows of resources', 'Used for reporting the deferred inflows of resources as recognized in the institution?s GPFS and in accordance with GASB 63.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Deferredoutflowsofresources', 'Deferred outflows of resources', 'Used for reporting the deferred outflows of resources as recognized in the institution?s GPFS and in accordance with GASB 63.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Depreciablecapitalassetsnetofdepreciation', 'Depreciable capital assets, net of depreciation', 'Used for reporting all capital assets reduced by the total accumulated depreciation, including improvements to land, easements, buildings, building improvements, vehicles, machinery, equipment, infrastructure, and all other tangible or intangible depreciable assets that are used in operations and that have initial useful lives extending beyond a single reporting period. Includes the net amount of all depreciable capital assets after reducing the gross amount for accumulated depreciation.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Longtermdebt', 'Long-term debt', 'Used for reporting the amount for long-term debt arising from debt issuance and lease-purchase agreements.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Longtermdebtcurrentportion', 'Long-term debt, current portion', 'Used for reporting the amount due in the next operating cycle (usually a year) for amounts otherwise reported as long-term or noncurrent debt. Includes only outstanding debt.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Netassetsinvestedincapitalassetsnetofrelateddebt', 'Net assets invested in capital assets, net of related debt', 'Used for reporting the component of net assets represented by the total of all capital assets, reduced by accumulated depreciation, and reduced by the amount of outstanding bonds, mortgages, notes, or other borrowings that are attributable to the acquisition, construction, or improvement of those assets (see indebtedness on capital assets). Some outstanding debt may be reported in both current and noncurrent liabilities.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Othercurrentliabilities', 'Other current liabilities', NULL, NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Othernoncurrentassets', 'Other noncurrent assets', NULL, NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Othernoncurrentliabilities', 'Other non-current liabilities', NULL, NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Restrictedexpendablenetassets', 'Restricted expendable net assets', 'Used for reporting restricted net assets that are expendable. Net assets should be reported as restricted when constraints placed on use are either (a) externally imposed by creditors, grantors, contributors, or laws and regulations of other governments or (b) imposed by law through constitutional provisions or enabling legislation. Expendable net assets are all those not required to be retained in perpetuity.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Restrictednonexpendablenetassets', 'Restricted non-expendable net assets', 'Used for reporting net assets that are restricted and nonexpendable. Net assets should be reported as restricted when constraints placed on use are either (a) externally imposed by creditors, grantors, contributors, or laws and regulations of other governments or (b) imposed by law through constitutional provisions or enabling legislation. Nonexpendable net assets are those that are required to be retained in perpetuity.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Totalcurrentassets', 'Total current assets', 'Used for reporting all current assets, including cash and cash equivalents, investments, accounts and notes receivables (net of allowance for uncollectible amounts), inventories, and all other assets classified as current assets.', NULL)
INSERT INTO dbo.[Ref_IPEDSGASB_Financial_Position]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Unrestrictednetassets', 'Unrestricted net assets', 'This should be the amount of net assets that do not meet the definition of ?restricted? or ?invested in capital assets, net of related debt.?', NULL)
END;

PRINT N'Populate Ref_IPEDSGASB_Functional_Expense table';
IF EXISTS(SELECT * FROM dbo.sysobjects WHERE id = object_id(N'[dbo].[Ref_IPEDSGASB_Functional_Expense]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
BEGIN
INSERT INTO dbo.[Ref_IPEDSGASB_Functional_Expense]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Instruction', 'Instruction', 'A functional expense category that includes expenses of the colleges, schools, departments, and other instructional divisions of the institution and expenses for departmental research and public service that are not separately budgeted. Includes general academic instruction, occupational and vocational instruction, community education, preparatory and adult basic education, and regular, special, and extension sessions. Also includes expenses for both credit and non-credit activities. Excludes expenses for academic administration where the primary function is administration (e.g., academic deans). Information technology expenses related to instructional activities if the institution separately budgets and expenses information technology resources are included (otherwise these expenses are included in academic support). Institutions include actual or allocated costs for operation and maintenance of plant, interest, and depreciation.', 1.00)
INSERT INTO dbo.[Ref_IPEDSGASB_Functional_Expense]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Research', 'Research', 'A functional expense category that includes expenses for activities specifically organized to produce research outcomes and commissioned by an agency either external to the institution or separately budgeted by an organizational unit within the institution. The category includes institutes and research centers, and individual and project research. This function does not include nonresearch sponsored programs (e.g., training programs). Also included are information technology expenses related to research activities if the institution separately budgets and expenses information technology resources (otherwise these expenses are included in academic support.) Institutions include actual or allocated costs for operation and maintenance of plant, interest, and depreciation.', 2.00)
INSERT INTO dbo.[Ref_IPEDSGASB_Functional_Expense]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Publicservice', 'Public service', 'A functional expense category that includes expenses for activities established primarily to provide noninstructional services beneficial to individuals and groups external to the institution. Examples are conferences, institutes, general advisory service, reference bureaus, and similar services provided to particular sectors of the community. This function includes expenses for community services, cooperative extension services, and public broadcasting services. Also includes information technology expenses related to the public service activities if the institution separately budgets and expenses information technology resources (otherwise these expenses are included in academic support). Institutions include actual or allocated costs for operation and maintenance of plant, interest, and depreciation.', 3.00)
INSERT INTO dbo.[Ref_IPEDSGASB_Functional_Expense]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Academicsupport', 'Academic support', 'A functional expense category that includes expenses of activities and services that support the institution''s primary missions of instruction, research, and public service. It includes the retention, preservation, and display of educational materials (for example, libraries, museums, and galleries); organized activities that provide support services to the academic functions of the institution (such as a demonstration school associated with a college of education or veterinary and dental clinics if their primary purpose is to support the instructional program); media such as audiovisual services; academic administration (including academic deans but not department chairpersons); and formally organized and separately budgeted academic personnel development and course and curriculum development expenses. Also included are information technology expenses related to academic support activities; if an institution does not separately budget and expense information technology resources, the costs associated with the three primary programs will be applied to this function and the remainder to institutional support. Institutions include actual or allocated costs for operation and maintenance of plant, interest, and depreciation.', 4.00)
INSERT INTO dbo.[Ref_IPEDSGASB_Functional_Expense]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Studentservices', 'Student services', 'A functional expense category that includes expenses for admissions, registrar activities, and activities whose primary purpose is to contribute to students emotional and physical well-being and to their intellectual, cultural, and social development outside the context of the formal instructional program. Examples include student activities, cultural events, student newspapers, intramural athletics, student organizations, supplemental instruction outside the normal administration, and student records. Intercollegiate athletics and student health services may also be included except when operated as self-supporting auxiliary enterprises. Also may include information technology expenses related to student service activities if the institution separately budgets and expenses information technology resources(otherwise these expenses are included in institutional support.) Institutions include actual or allocated costs for operation and maintenance of plant, interest, and depreciation.', 5.00)
INSERT INTO dbo.[Ref_IPEDSGASB_Functional_Expense]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Institutionalsupport', 'Institutional support', 'A functional expense category that includes expenses for the day-to-day operational support of the institution. Includes expenses for general administrative services, central executive-level activities concerned with management and long range planning, legal and fiscal operations, space management, employee personnel and records, logistical services such as purchasing and printing, and public relations and development. Also includes information technology expenses related to institutional support activities. If an institution does not separately budget and expense information technology resources, the IT costs associated with student services and operation and maintenance of plant will also be applied to this function.', 6.00)
INSERT INTO dbo.[Ref_IPEDSGASB_Functional_Expense]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Auxiliaryenterprises', 'Auxiliary enterprises', 'Expenses for essentially self-supporting operations of the institution that exist to furnish a service to students, faculty, or staff, and that charge a fee that is directly related to, although not necessarily equal to, the cost of the service. Examples are residence halls, food services, student health services, intercollegiate athletics (only if essentially self-supporting), college unions, college stores, faculty and staff parking, and faculty housing. Institutions include actual or allocated costs for operation and maintenance of plant, interest and depreciation.', 7.00)
INSERT INTO dbo.[Ref_IPEDSGASB_Functional_Expense]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Scholarshipsfellowships', 'Scholarships and fellowships', 'That portion of scholarships and fellowships granted that exceeds the amount applied to institutional charges such as tuition and fees or room and board. The amount reported as expense excludes allowances and discounts. The FASB survey uses the term "net grants in aid to students" rather than "scholarships and fellowships."', 8.00)
INSERT INTO dbo.[Ref_IPEDSGASB_Functional_Expense]([Code], [Description], [Definition], [Sort_Order])
VALUES ('Hospitalservices', 'Hospital services', 'Expenses associated with a hospital operated by the postsecondary institution (but not as a component unit) and reported as a part of the institution. This classification includes nursing expenses, other professional services, general services, administrative services, and fiscal services. Also included are information technology expenses, actual or allocated costs for operation and maintenance of plant, interest and depreciation related to hospital capital assets.', 9.00)
INSERT INTO dbo.[Ref_IPEDSGASB_Functional_Expense]([Code], [Description], [Definition], [Sort_Order])
